$('.progressBar__list_item').hover(
    function(){
        console.log('work');
        $('.progressBar__list_item.active').removeClass("active");
        $(this).addClass("active");
        let step;

        if ($(this).index() > 3 && $(this).index() <= 0){
            return;
        } else if($(this).index() > 0){
            step = $(this).index() * 20 + '%';
            $('.progressBar__line').css('width', step);
            
        }
    }
);

